// Input: the previous node is "Drive - Create Parent"
const parentId = $input.first().json.id; // id from the Drive 'Create' result

// Get the updated row again (from the trigger)
const row = $("Google Sheets Trigger").first().json;
const base = row.Title;

const names = [
  `${base} - 01 Research`,
  `${base} - 02 Briefing`,
  `${base} - 03 Copy`,
  `${base} - 04 Beeld`,
  `${base} - 05 Review`,
  `${base} - 06 Implementatie`,
  `${base} - 07 Performance`,
];

// Return 7 items: one per child folder
return names.map(n => ({ json: { folderName: n, parentId } }));

