// Node name suggestion: "Build proposal files"
const res = $input.first().json.output;            // from Research Agent
const childFolders = $('Create ChildFolders').all(); // the 7 created folders

// Helper: pick folder by suffix; default to first if not found
const idBySuffix = (suffix) => {
  const m = childFolders.find(i => (i.json.name ?? i.json.title ?? i.json.folderName ?? '').endsWith(suffix));
  return (m ?? childFolders[0]).json.id;
};

// Put proposals in 01 Research (change suffix if you prefer 02/03/etc.)
const folderId = idBySuffix('01 Research');

const body = (o, proposal) =>
`${o.meta.topic}

${o.meta.confidence_note}

## Summary

${o.research_summary}

## Argumenten

${o.key_arguments_markdown}

## Bronnen

${o.sources_markdown}

## Voorstel

${proposal}`;

return [
  { json: { fileName: `${res.meta.topic}-research-voorstel-1`, content: body(res, res.proposal_1), folderId } },
  { json: { fileName: `${res.meta.topic}-research-voorstel-2`, content: body(res, res.proposal_2), folderId } },
  { json: { fileName: `${res.meta.topic}-research-voorstel-3`, content: body(res, res.proposal_3), folderId } },
];
