parameters.jsCode:
// Get the raw output from previous node
const rawOutput = $input.first().json?.output || $input.first().json || '';
// Step 1: First try to extract from code blocks
let jsonText;
const codeBlockMatch = rawOutput.toString().match(/```(?:json)?\s*([\s\S]*?)\s*```/);
if (codeBlockMatch && codeBlockMatch[1]) {
  jsonText = codeBlockMatch[1];
} else {
  // If no code block, use the raw output
  jsonText = rawOutput.toString();
}
// Step 2: Replace escaped newlines and clean whitespace
jsonText = jsonText.replace(/\\n/g, ' ').replace(/\s+/g, ' ').trim();
// Function to make any text JSON-safe
function makeJsonSafe(text) {
  if (text === undefined || text === null) return '';

  return text.toString()
    .replace(/\n/g, ' ')           // Replace newlines with spaces
    .replace(/\r/g, ' ')           // Replace carriage returns
    .replace(/\t/g, ' ')           // Replace tabs
    .replace(/\s+/g, ' ')          // Replace multiple spaces with single space
    .replace(/[\u0000-\u001F]/g, ' ') // Replace control characters
    .trim();                       // Trim leading/trailing whitespace
}
// Function to handle arrays safely
function makeArraySafe(arr, joinChar) {
  if (!Array.isArray(arr)) return '';
  return arr.map(item => makeJsonSafe(item)).join(joinChar);
}
// Step 3: Try to parse the JSON with extra error handling
try {
  const parsedData = JSON.parse(jsonText);

  // Step 4: Extract data with nested try/catch blocks for maximum safety
  let formatted = { success: false };

  try {
    // Process focused topic if it exists
    if (parsedData.focused_topic) {
      formatted.topic_title = makeJsonSafe(parsedData.focused_topic.title || '');
      formatted.topic_keywords = makeArraySafe(parsedData.focused_topic.keywords || [], ', ');
      formatted.topic_key_questions = makeArraySafe(parsedData.focused_topic.key_questions || [], '; ');
    }

    // Process SEO recommendations if they exist
    if (parsedData.seo_recommendations) {
      formatted.seo_primary_keyword = makeJsonSafe(parsedData.seo_recommendations.primary_keyword || '');
      formatted.seo_secondary_keywords = makeArraySafe(parsedData.seo_recommendations.secondary_keywords || [], ', ');
      formatted.seo_suggested_titles = makeArraySafe(parsedData.seo_recommendations.suggested_title_formats || [], '; ');
    }

    // Create a simple flat content string with NO newlines
    formatted.formatted_content = 
      'Topic: ' + formatted.topic_title + ' | ' +
      'Keywords: ' + formatted.topic_keywords + ' | ' +
      'Key Questions: ' + formatted.topic_key_questions + ' | ' +
      'SEO Primary Keyword: ' + formatted.seo_primary_keyword + ' | ' +
      'Secondary Keywords: ' + formatted.seo_secondary_keywords + ' | ' +
      'Suggested Titles: ' + formatted.seo_suggested_titles;

    // Mark as successful
    formatted.success = true;

  } catch (dataError) {
    // If data extraction failed, return a safe error object
    return {
      json: {
        success: false,
        error: 'Data extraction error: ' + makeJsonSafe(dataError.message),
        data_error: true
      }
    };
  }

  // Return the formatted data
  return { json: formatted };

} catch (jsonError) {
  // If JSON parsing failed completely, return error info
  return {
    json: {
      success: false,
      error: 'JSON parsing error: ' + makeJsonSafe(jsonError.message),
      json_error: true,
      cleaned_text: makeJsonSafe(jsonText).substring(0, 200) + '...'
    }
  };
}

// Get the input
const input = $input.first().json;
// Extract the JSON content from the array structure
function extractData() {
  try {
    // Find the JSON part - look for the content between code blocks
    let jsonContent = '';

    if (Array.isArray(input) && input[0] && input[0].output) {
      const match = input[0].output.match(/```json\n([\s\S]*?)\n```/);
      if (match && match[1]) {
        jsonContent = match[1];
      }
    } else if (input && input.output) {
      const match = input.output.match(/```json\n([\s\S]*?)\n```/);
      if (match && match[1]) {
        jsonContent = match[1];
      }
    }

    // Parse the JSON
    const parsed = JSON.parse(jsonContent);

    // Extract just what we need - clean and simple
    return {
      title_text: parsed.title.text.replace(/\n/g, ' ').trim(),
      introduction_text: parsed.introduction.text.replace(/\n/g, ' ').trim(),
      chapter_1_text: parsed.chapter_1.text.replace(/\n/g, ' ').trim(),
      chapter_2_text: parsed.chapter_2.text.replace(/\n/g, ' ').trim()
    };
  } catch (error) {
    // Simple error handling
    return {
      error: 'Couldn\'t parse the input: ' + error.message
    };
  }
}
// Get the clean data and return it
return {
  json: extractData()
};

// IMPROVED HTML JOINER - Combines all HTML sections and formats for email
// Get each formatted HTML section
const writerHTML = $('Write Content - HTML Cleanup').first().json.output || '';
const factCheckerHTML = $('Fact Checker - JSON to HTML').first().json.output || '';
const correctionsHTML = $('Correction Workflow - JSON to HTML').first().json.output || '';
const citationsHTML = $('Deep Research Citations - JSON to HTML').first().json.output || '';
const SEOReportHTML = $('SEO Optimizer - JSON to HTML').first().json.output || '';
const contentOutlineHTML = $('Write Content Outline - JSON to HTML').first().json.output || '';
const deepresearchreportHTML = $('Deep Research Report - JSON to HTML').first().json.output || '';

// Build an array of all available sections
const sections = [];

// Add the main article first (always include this)
sections.push(`
  <div style="background-color: #f0f8ff; padding: 20px; border-left: 6px solid #1e90ff;">
    <h2 style="font-size: 26px; color: #1e90ff; margin-top: 0;">Main Article</h2>
    ${writerHTML}
  </div>
`);

// Add other sections if they exist
if (factCheckerHTML) sections.push(factCheckerHTML);
if (correctionsHTML) sections.push(correctionsHTML);
if (citationsHTML) sections.push(citationsHTML);
if (SEOReportHTML) sections.push(SEOReportHTML);
if (contentOutlineHTML) sections.push(contentOutlineHTML);
if (deepresearchreportHTML) sections.push(deepresearchreportHTML);

// Create final HTML with proper styling and responsive layout
const finalHTML = `
<!DOCTYPE html>
<html>
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Voorstel artikel</title>
</head>
<body style="font-family: Arial, sans-serif; line-height: 1.6; color: #333; max-width: 1200px; margin: 0 auto; padding: 20px;">

  ${sections.join('<hr style="border: 0; height: 1px; background: #ddd; margin: 30px 0;">')}

  <div style="margin-top: 40px; padding-top: 20px; border-top: 1px solid #ddd; text-align: center; font-size: 14px; color: #666;">
    <p>© ${new Date().getFullYear()} Timo de Groot. All rights reserved.</p>
  </div>
</body>
</html>
`;

// Return both the full HTML and the individual sections
return [{
  json: {
    html: finalHTML,
    htmlSections: sections
  }
}];

