// Key fix: NEVER unescape before JSON.parse. Parse first; then render.

const esc = (s) =>
  (s ?? '').toString().replace(/[&<>"']/g, (c) => ({'&':'&amp;','<':'&lt;','>':'&gt;','"':'&quot;',"'":'&#39;'}[c]));

const pick = (obj, path, fallback=null) => {
  try {
    return path.split('.').reduce((o,k)=> (o?.[k]), obj) ?? fallback;
  } catch { return fallback; }
};

// Extract JSON inside ```json fences, but DO NOT mutate escapes pre-parse
const extractJsonString = (txt) => {
  if (typeof txt !== 'string') return null;
  const m = txt.match(/```json\s*([\s\S]*?)\s*```/i) || txt.match(/```\s*json\s*([\s\S]*?)\s*```/i);
  if (m && m[1]) return m[1].trim();
  const t = txt.trim();
  return (t.startsWith('{') && t.endsWith('}')) ? t : null;
};

const parseMaybeFencedJson = (payload) => {
  try {
    // Case: array [{ output: "```json { ... } ```" }]
    if (Array.isArray(payload) && payload[0]?.output) {
      const js = extractJsonString(payload[0].output);
      if (js) return JSON.parse(js);
    }
    // Case: object { output: "```json { ... } ```" }
    if (payload && typeof payload === 'object' && typeof payload.output === 'string') {
      const js = extractJsonString(payload.output);
      if (js) return JSON.parse(js);
    }
    // Case: string directly
    if (typeof payload === 'string') {
      const js = extractJsonString(payload);
      if (js) return JSON.parse(js);
    }
    // Already parsed object
    if (payload && typeof payload === 'object') return payload;
  } catch (e) {
    // Fallback: try a safer minimal cleanup only if needed
    try {
      const raw = (Array.isArray(payload) ? payload[0]?.output : payload?.output) || payload;
      const js2 = extractJsonString(String(raw || ''));
      if (js2) return JSON.parse(js2);
    } catch {}
  }
  return {};
};

// Tiny markdown→HTML for Output 3; strip <think>…</think>
function mdToHtml(md) {
  if (!md || typeof md !== 'string') return '';
  md = md.replace(/<think>[\s\S]*?<\/think>\s*/gi, '').replace(/\r\n/g, '\n');
  let s = esc(md);
  s = s.replace(/^###\s+(.*)$/gm, '<h3>$1</h3>')
       .replace(/^##\s+(.*)$/gm, '<h2>$1</h2>')
       .replace(/^#\s+(.*)$/gm, '<h1>$1</h1>')
       .replace(/\*\*(.+?)\*\*/g, '<strong>$1</strong>')
       .replace(/\*(.+?)\*/g, '<em>$1</em>')
       .replace(/^(?:-|\*)\s+(.*)$/gm, '<li>$1</li>')
       .replace(/(?:\s*<li>[\s\S]*?<\/li>\s*)+/g, (b)=>`<ul>${b}</ul>`);
  s = s.split(/\n{2,}/).map(ch => (/^\s*<(h1|h2|h3|ul|li|p|hr)/i.test(ch) ? ch : `<p>${ch.replace(/\n/g,'<br>')}</p>`)).join('\n');
  return s;
}

