options.customCss:
:root {
  /* Typography */
  --font-family: 'Open Sans', sans-serif;
  --font-weight-normal: 400;
  --font-weight-bold: 600;

  --font-size-body: 14px;
  --font-size-label: 16px;
  --font-size-input: 16px;
  --font-size-header: 24px;
  --font-size-paragraph: 16px;
  --font-size-subheader: 18px;
  --font-size-test-notice: 14px;

  /* HTML Headings */
  --font-size-html-h1: 32px;
  --font-size-html-h2: 28px;
  --font-size-html-h3: 22px;
  --font-size-html-h4: 18px;
  --font-size-html-h5: 16px;
  --font-size-html-h6: 14px;

  /* Brand Colors */
  --color-primary: #1F1672;       /* Extracted from logo */
  --color-secondary: #342b93;     /* Slightly lighter version for highlights if needed */

  --color-background: #ffffff;
  --color-page-bg: #f5f6fa;

  --color-card-bg: #ffffff;
  --color-card-border: #e0e2e7;
  --color-card-shadow: rgba(31, 22, 114, 0.06);

  --color-link: var(--color-primary);
  --color-header: var(--color-primary);
  --color-label: #5a5d6a;

  --color-input-border: #d0d4e0;
  --color-input-text: #333;
  --color-focus-border: var(--color-primary);

  --color-submit-btn-bg: var(--color-primary);
  --color-submit-btn-text: #ffffff;

  --color-error: #ea1f30;
  --color-required: var(--color-primary);
  --color-clear-button-bg: #999ca8;

  --color-test-notice-text: var(--color-primary);
  --color-test-notice-bg: #f3f2fa;  /* Very light purple background */
  --color-test-notice-border: #d6d3f0;

  --color-html-text: #333333;
  --color-html-link: var(--color-primary);
  --color-header-subtext: #888fa4;

  /* Border Radii */
  --border-radius-card: 8px;
  --border-radius-input: 6px;
  --border-radius-clear-btn: 50%;

  /* Spacing */
  --padding-container-top: 32px;
  --padding-card: 32px;
  --padding-test-notice-vertical: 14px;
  --padding-test-notice-horizontal: 28px;
  --margin-bottom-card: 20px;
  --padding-form-input: 14px;

  /* Dimensions */
  --container-width: 480px;
  --submit-btn-height: 52px;
  --checkbox-size: 20px;

  /* Shadows */
  --box-shadow-card: 0 4px 16px 0 var(--color-card-shadow);
  --opacity-placeholder: 0.5;
}
